import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { GreetingGenerationRequest, GreetingGenerationResponse } from './proto/greeting_service_pb';
import { ImageServiceClient } from './proto/Greeting_serviceServiceClientPb';
import { ClipLoader } from 'react-spinners';

function App() {
  const [imagePrompt, setImagePrompt] = useState('');
  const [messagePrompt, setMessagePrompt] = useState('');
  const [response, setResponse] = useState<GreetingGenerationResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const client = new ImageServiceClient("http://localhost:8080");

  const handleSubmit = async () => {
    if (messagePrompt.length > 32) {
      setError("Message prompt should be 32 characters or less");
      return;
    }

    const req = new GreetingGenerationRequest();
    req.setImagePrompt(imagePrompt);
    req.setMessagePrompt(messagePrompt);

    setLoading(true);
    setError(null);
    setResponse(null);

    try {
      const resp = await client.generateGreeting(req, {});
      setResponse(resp);
      setLoading(false);
    } catch (err) {
      setError("Failed to generate greeting");
      console.error(err);
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Greeting Generator</h1>
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter image prompt"
            value={imagePrompt}
            onChange={(e) => setImagePrompt(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter message prompt (32 chars max)"
            value={messagePrompt}
            onChange={(e) => setMessagePrompt(e.target.value)}
            maxLength={32}
          />
          <button onClick={handleSubmit}>Generate Greeting</button>
        </div>
        {loading && <ClipLoader color="#ffffff" />}
        {error && <p className="error">{error}</p>}
        {response && (
          <div className="response">
            <img src={"generated_images/sad_m_smoki.png"} alt="Generated Greeting" />
          </div>
        )}
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
